ggplot(read.csv('progs.csv'), aes(y=Total...Session, x=Group)) + geom_boxplot(varwidth = TRUE, outlier.shape = 4, outlier.alpha = 0.5) + labs(title="Number of Programs Issued\nby Control vs Experimental Group",y = "Programs") + theme(plot.title = element_text(hjust = 0.5), axis.title.x=element_blank()) + scale_x_discrete(limits = c("I", "G")), labels = c("Control Group","Experimental Group"))

ggplot(read.csv('avgSlength.csv'), aes(y=Average.Session.Length, x=Group)) + geom_boxplot(varwidth = TRUE, outlier.shape = 4, outlier.alpha = 0.5) + labs(title="Average Length of Programs Issued\nby Control vs Experimental Group",y = "SEQ") + theme(plot.title = element_text(hjust = 0.5), axis.title.x=element_blank()) + scale_x_discrete(limits = c("I", "G"), labels = c("Control Group","Experimental Group"))

ggplot(read.csv('avgLoopsS.csv'), aes(y=Average...Loops.Session, x=Group)) + geom_boxplot(varwidth = TRUE, outlier.shape = 4, outlier.alpha = 0.5) + labs(title="Average Number of Loops in Programs Issued\nby Control vs Experimental Group",y = "CYC") + theme(plot.title = element_text(hjust = 0.5), axis.title.x=element_blank()) + scale_x_discrete(limits = c("I", "G"), labels = c("Control Group","Experimental Group"))
